import { Table, Button } from '@douyinfe/semi-ui';
import { IconDelete } from '@douyinfe/semi-icons';

class TableApp extends React.Component {
    constructor(props) {
        super(props);

        this.raw = [
            {
                key: '1',
                name: 'John Brown',
                age: 32,
                address: 'New York No. 1 Lake Park, New York No. 1 Lake Park',
            },
            {
                key: '2',
                name: 'Jim Green',
                age: 42,
                address: 'London No. 1 Lake Park',
            },
            {
                key: '3',
                name: 'Joe Black',
                age: 32,
                address: 'Sidney No. 1 Lake Park',
            },
            {
                key: '4',
                name: 'Michael James',
                age: 99,
                address: 'Sidney No. 1 Lake Park',
            },
        ];

        this.state = {
            dataSource: [...this.raw],
            columns: [
                {
                    title: 'Name',
                    dataIndex: 'name',
                    width: 200,
                    render: text => <a>{text}</a>,
                },
                {
                    width: 90,
                    title: 'Age',
                    dataIndex: 'age',
                },
                {
                    title: 'Address',
                    dataIndex: 'address',
                },
                {
                    title: 'Operation',
                    width: 150,
                    render: (text, record) => (
                        <Button icon={<IconDelete />}theme="borderless" onClick={() => this.removeRecord(record.key)} />
                    ),
                },
            ],
        };
    }

    removeRecord(key) {
        let dataSource = [...this.state.dataSource];
        if (key != null) {
            let idx = dataSource.findIndex(data => data.key === key);

            // console.log(key, dataSource, idx);

            if (idx > -1) {
                dataSource.splice(idx, 1);
                this.setState({ dataSource });
            }
        }
    }

    resetData() {
        let dataSource = [...this.raw];
        this.setState({ dataSource });
    }

    render() {
        let { columns, dataSource } = this.state;

        return (
            <>
                <Button onClick={() => this.resetData()} style={{ marginBottom: 10 }}>
                    Reset
                </Button>
                <Table columns={columns} dataSource={dataSource} pagination={false} />
            </>
        );
    }
}

render(TableApp);
