  }, "import React from 'react';
import { Table } from '@douyinfe/semi-ui';

class App extends React.Component {
    constructor() {
        const columns = [
            {
                title: 'Name',
                dataIndex: 'name',
            },
            {
                title: 'Age',
                dataIndex: 'age',
            },
            {
                title: 'Address',
                dataIndex: 'address',
            },
        ];

        const data = [];
        for (let i = 0; i < 46; i++) {
            data.push({
                key: '' + i,
                name: `Edward King ${i}`,
                age: 32,
                address: `London, Park Lane no. ${i}`,
            });
        }
        this.data = data;

        this.fetchData = (currentPage = 1) => {
            // console.log(`FetchData currentPage: `, currentPage);
            this.setState({ loading: true });
            let pagination = { ...this.state.pagination, currentPage };
            return new Promise((res, rej) => {
                setTimeout(() => {
                    let dataSource = this.data.slice(
                        (currentPage - 1) * pagination.pageSize,
                        currentPage * pagination.pageSize
                    );
                    res(dataSource);
                }, 1500);
            }).then(dataSource => {
                // console.log('Request data: ', dataSource);
                this.setState({
                    loading: false,
                    pagination,
                    dataSource,
                });
            });
        };

        this.state = {
            loading: false,
            columns,
            pagination: {
                currentPage: 1,
                pageSize: 5,
                total: data.length,
                onPageChange: page => this.fetchData(page),
            },
            dataSource: [],
        };
    }

    componentDidMount() {
        this.fetchData();
    }

    render() {
        let { columns, dataSource, pagination, loading } = this.state;

        return <Table columns={columns} dataSource={dataSource} pagination={pagination} loading={loading} />;
    }
}

render(App);
